import React, { useState, useEffect } from "react";
import OrderDetails from "./OrderDetails";
import { Link } from 'react-router-dom';
import { useLocation } from "react-router-dom";
import axios from 'axios';

function Orders(props) {
  const location = useLocation()
  console.log(location.state.id)

  let [value, setValue] = useState(0)

  var elements =  []

  function orderList() {
    axios({
      url: `http://localhost:8089/customers/${location.state.id}/orders`,
      method: 'get',
  }).then(res => {
      const data = res.data;
      console.log("asma"+value) 
      elements = data;
      var table = `<table>
      <tr>
        <th><img>Order Id</img></th>
        <th>Order By</th>
        <th>Order Date</th>
      </tr>`;
      elements.map(order => table += `
      <tr>
      <td><Link to='/orderDetails/' state={{ id: ${order.orderId}}}>${order.orderId}</Link></td>
      <td>${order.orderBy}</td>
      <td>${order.orderDate}</td>
      </tr>`
      );
      table += `</table>`;

      value = { table };
      setValue(table)
     // setState({data})
      console.log("sooo"+JSON.stringify(data))
  }).catch(err => {
      console.log(err)
  });
  }
  useEffect(() => {
    console.log("Order")
    orderList()
  }, [""])


  return (
    <React.Fragment>
      <div className="flex-vertical" style={{ marginLeft: "20px" }}>
        <header>
          <h1>Order Lists</h1>
        </header>
        <div dangerouslySetInnerHTML={{__html: value}}/>


      </div>
    </React.Fragment>
  );

}

export default Orders;
