import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { Link } from 'react-router-dom';
import Orders from "./Orders";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useParams } from "react-router-dom";

function CustomerDetails(props) {
    const location = useLocation()
    console.log(location.state.id)

    const [state, setState] = useState('')

    function getCustomerList() {

        axios({
            url: `http://localhost:8089/customers/${location.state.id}`,
            method: 'get',
        }).then(res => {
            const data = res.data;
            var elements = ``
            setState({data})
            console.log("sooo"+JSON.stringify(data))
        }).catch(err => {
            console.log(err)
        });

    }

    useEffect(() => {
        console.log("Customer Details")
        getCustomerList()
    }, [""])

    return (

        <React.Fragment>
            <div className="flex-vertical" style={{ marginLeft: "20px" }}>

                <header>
                    <h1>Customer Details</h1>
                </header>


                <Link to="/orders" state={{ id: location.state.id }}>  <button>  Orders</button> </Link>

            </div>
        </React.Fragment>
    );

}

export default CustomerDetails;
