import React from "react";
import { useLocation } from "react-router-dom";
import { Link } from 'react-router-dom';
import Orders from "./Orders";
class CustomerDetails extends React.Component {



    constructor() {
        super();
        this.state = { test: 'Customer lists loading...', elements: [] }
      }

    componentDidMount() {
        this.getCustomerList()
      }

      getCustomerList() {
        var table = "";
        for (var i = 0; i < 5; i++) {
          table += `<section id="${i}"><p>
            ASMA Batool
            </p>`;
        }

        this.setState({
          test: <div dangerouslySetInnerHTML={{ __html: table }} />
        })
      } 

      render() {
        return (
          <React.Fragment>
           <div  className="flex-vertical" style={{marginLeft: "20px"}}>

           <header>
            <h1>Customer Details</h1>
          </header>

         {this.state.test}

         <Link
         to={{
         pathname: "/orders",
         }}>  <button href="/orders">  Orders</button> </Link>


             {/* <button style={{width: "100px"}} onClick={this.orderDetails}>Orders</button> */}
             </div>      
          </React.Fragment>
        );
      }
    }

  export default CustomerDetails;
