import React from "react";
import CustomerDetails from "./CustomerDetails";
import axios from 'axios';
import { Link } from "react-router-dom";


class Home extends React.Component {

  constructor() {
    super();
    this.state = { elements: [] }
  }

  componentDidMount() {
    this.getCustomerList()
  }

  getCustomerList() {

    axios({
      url: "http://localhost:8089/customers",
      method: 'get',
    }).then(res => {

      this.state.elements = res.data

      console.log(this.state.elements)

      this.setState({
        elements: this.state.elements
      })
    }).catch(err => {

    });

  }

  render() {
    return (
      <React.Fragment>

        <div className="flex-vertical" >

          <header>
            <h2>Customer Lists</h2>
          </header>
          <table>
            <tr>
              <th>Customer Id</th>
              <th>Customer Name</th>
              <th>Phone Number</th>
            </tr>
          {this.state.elements.map(data => <tr><td>{data.customerId}</td><td><Link key={data.customerId} to='/child/' state={{ id: data.customerId }}>{data.customerName}</Link></td><td>{data.phoneNumber}</td></tr>)}
          </table>
        </div>
      </React.Fragment>
    );
  }
}

export default Home;
