import React from "react";
import { useLocation } from "react-router-dom";
import { Link } from 'react-router-dom';
import Orders from "./Orders";
class CustomerDetails extends React.Component {



    constructor() {
        super();
        this.state = { test: 'Customer lists loading...', elements: [] }
      }

    componentDidMount() {
        this.getCustomerList()
      }

      getCustomerList(){

        axios({
          url: "http://10.10.135.70:8080/customers",
          method: 'get',
      }).then(res => {
          let data = res.data
          var elements = ``
          data.map((element) => {

            elements += `<section id="${1}"><p>
          <Link
          to={{
          pathname: "/child",
          data-name = "jigar"
          }}>  <a href="/child"> ${element.customerName} </a> </Link>
            </p>`;

              // elements += `<p>${element.customerName}</p>`
          });
          console.log(elements)
          this.setState({
            test: <div dangerouslySetInnerHTML={{ __html: elements }}/>
          })
      }).catch( err => {
          console.log(err)
      });

    }

      render() {
        return (
          <React.Fragment>
           <div  className="flex-vertical" style={{marginLeft: "20px"}}>

           <header>
            <h1>Customer Details</h1>
          </header>

         {this.state.test}

         <Link
         to={{
         pathname: "/orders",
         }}>  <button href="/orders">  Orders</button> </Link>


             {/* <button style={{width: "100px"}} onClick={this.orderDetails}>Orders</button> */}
             </div>      
          </React.Fragment>
        );
      }
    }

  export default CustomerDetails;
