import React, { useState, useEffect } from "react";
import OrderDetails from "./OrderDetails";
import { Link } from 'react-router-dom';
import { useLocation } from "react-router-dom";
import axios from 'axios';

function Orders(props) {
  const location = useLocation()
  console.log(location.state.id)

  let [value, setValue] = useState(0)

  var elements =  []

  function orderList() {
    axios({
      url: `http://localhost:8089/customers/${location.state.id}/orders`,
      method: 'get',
  }).then(res => {
      const data = res.data;
      console.log("asma"+value) 
      elements = data;
      elements.map(va => console.log(va.orderId));

      value = { elements };
      setValue({elements: elements})
     // setState({data})
      console.log("sooo"+JSON.stringify(data))
  }).catch(err => {
      console.log(err)
  });
  }
  useEffect(() => {
    console.log("Order")
    orderList()
  }, [""])


  return (
    <React.Fragment>
      <div className="flex-vertical" style={{ marginLeft: "20px" }}>
        <header>
          <h1>Order Lists</h1>
        </header>

        //{value.map(data => <Link key={data.orderId} to="/orderDetails" state={{ id: data.orderId }}>{data.orderId}</Link>)}

      </div>
    </React.Fragment>
  );

}

export default Orders;
