import React from "react";
import CustomerDetails from "./CustomerDetails";
import axios from 'axios';


class Home extends React.Component {

  constructor() {
    super();
    this.state = {  elements: 'Customer lists loading...' }
  }

  componentDidMount() {
    this.getCustomerList()
  }

  getCustomerList(){
        ``
    axios({
      url: "http://10.10.135.70:8080/customers",
      method: 'get',
  }).then(res => {
      let data = res.data
      var elements = ``
      section = ``
      data.map((element) => {

        elements += `<li>
        ${element.customerName}
      <Link
      to={{
      pathname: "/child",
      data-name = "${element.customerId}"
      }}>  ${element.customerName} </Link>
        </li>`;

      });
      console.log(elements)
      this.setState({
        elements: this.state.elements
      })
  }).catch( err => {
      console.log(err)
  });

}

  render() {
    return (
      <React.Fragment>

       <div  className="flex-vertical" style={{marginLeft: "100px"}}>

       <header>
        <h2>Customer Lists</h2>
      </header>
         {this.state.elements}
         </div>      
      </React.Fragment>
    );
  }
}

export default Home;
