import React from "react";
import CustomerDetails from "./CustomerDetails";
import axios from 'axios';
import { Link } from "react-router-dom";


class Home extends React.Component {

  constructor() {
    super();
    this.state = {  elements: [] }
  }

  componentDidMount() {
    this.getCustomerList()
  }

  getCustomerList(){

    axios({
      url: "http://10.10.135.70:8080/customers",
      method: 'get',
  }).then(res => {

      this.state.elements = res.data

      console.log(this.state.elements)

      this.setState({
        elements: this.state.elements
      })
  }).catch( err => {
      console.log(err)
  });

}

  render() {
    return (
      <React.Fragment>

       <div  className="flex-vertical" style={{marginLeft: "100px"}}>

       <header>
        <h2>Customer Lists</h2>
      </header>
        {this.state.elements.map(data => <Link to={`/child?${data.customerId}`} >{data.customerName}</Link>)}
        {/* {this.state.elements.length == 0?"Customers list is loading..":} */}
         </div>      
      </React.Fragment>
    );
  }
}

export default Home;
