import React, { useState, useEffect } from "react";
import OrderDetails from "./OrderDetails";
import { Link } from 'react-router-dom';
import { useLocation } from "react-router-dom";

function Orders(props) {
  const location = useLocation()
  console.log(location.state.id)

  const [value, setValue] = useState(0)

  function orderList() {
    axios({
      url: `http://localhost:8089/customers/${location.state.id}`,
      method: 'get',
  }).then(res => {
      const data = res.data;

      console.log("asma"+state) 
      elements = ` <table>
      <tr>
        <th>Customer Id</th>
        <td>${data.customerId}</td>
      </tr>
      <tr>
      <th>Customer Name</th>
      <td>${data.customerName}</td>
      </tr>
      <tr>
      <th>Phone Number</th>
      <td>${data.phoneNumber}</td>
      </tr>
      </table>`
     state = { elements };
      setState(elements)
     // setState({data})
      console.log("sooo"+JSON.stringify(data))
  }).catch(err => {
      console.log(err)
  });

    var table = "";
    for (var i = 0; i < 5; i++) {
      table += `<section id="${i}"><p>
            <Link
            to={{
            pathname: "/orderDetails",
            data-name = "jigar"
            }}>  <a href="/orderDetails"> ${i} Checken</a> </Link>
              </p>`;
    }
    setValue(value + 1)
  }
  useEffect(() => {
    console.log("Order")
    orderList()
  }, [""])


  return (
    <React.Fragment>
      <div className="flex-vertical" style={{ marginLeft: "20px" }}>
        <header>
          <h1>Order Lists</h1>
        </header>

        {[1, 2, 3, 4, 5].map(data => <Link key={data} to="/orderDetails" state={{ id: data }}>{data} Checken</Link>)}

      </div>
    </React.Fragment>
  );

}

export default Orders;
