import React from "react";
import CustomerDetails from "./CustomerDetails";
import axios from 'axios';


class Home extends React.Component {

  constructor() {
    super();
    this.state = {  elements: [] }
  }

  componentDidMount() {
    this.getCustomerList()
  }

  getCustomerList(){

    axios({
      url: "http://10.10.135.70:8080/customers",
      method: 'get',
  }).then(res => {
      let data = res.data
      this.state.elements = []

      data.map((element) => {

        this.state.elements.append(element)

      });

      console.log(this.state.elements)

      this.setState({
        elements: this.state.elements
      })
  }).catch( err => {
      console.log(err)
  });

}

  render() {
    return (
      <React.Fragment>

       <div  className="flex-vertical" style={{marginLeft: "100px"}}>

       <header>
        <h2>Customer Lists</h2>
      </header>
         <div>{this.state.elements}</div>
         </div>      
      </React.Fragment>
    );
  }
}

export default Home;
