import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

function OrderDetails(props) {

  const [count, setCount] = useState(0)

  const location = useLocation()
  console.log(location.state.orderId)
  console.log(location.state.customerId)

  function getOrderDetails() {

    axios({
      url: `http://localhost:8089/customers//${location.state.customerId}/orders/${location.state.orderId}`,
      method: 'get',
  }).then(res => {
      const data = res.data;
      console.log("asma"+state) 
      elements = ` <table>
      <tr>
        <th>Order Id</th>
        <td>${data.orderId}</td>
      </tr>
      <tr>
      <th>Order By</th>
      <td>${data.orderBy}</td>
      </tr>
      <tr>
      <th>Order Date</th>
      <td>${data.orderDate}</td>
      </tr>
      </table>`
     state = { elements };
      setState(elements)
     // setState({data})
      console.log("sooo"+JSON.stringify(data))
    }).catch(err => {
      console.log(err)
    });

    setCount(count + 1)

  }

  useEffect(() => {
    console.log("Order Details")
    getOrderDetails()
  }, [""])



  return (
    <React.Fragment>
      <div className="flex-vertical" style={{ marginLeft: "20px" }}>

        <header>
          <h1 onClick={() => setCount(count + 1)}>Orders Details</h1>
        </header>

      </div>
    </React.Fragment>
  );

}

export default OrderDetails;
