import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { Link } from 'react-router-dom';
import Orders from "./Orders";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useParams } from "react-router-dom";
import ReactHtmlParser from "react-html-parser";

function CustomerDetails(props) {
    const location = useLocation()
    console.log(location.state.id)

    let [state, setState] = useState('')

    var elements = ``

    function getCustomerList() {

        axios({
            url: `http://localhost:8089/customers/${location.state.id}`,
            method: 'get',
        }).then(res => {
            const data = res.data;

            console.log("asma"+state) 
            elements = `<p>${data.customerId}</p>`
           state = <div dangerouslySetInnerHTML={{ __html: elements }}/>
            setState(elements)
           // setState({data})
            console.log("sooo"+JSON.stringify(data))
        }).catch(err => {
            console.log(err)
        });

    }

    useEffect(() => {
        console.log("Customer Details")
        getCustomerList()
    }, [""])

    return (

        <React.Fragment>
            <div className="flex-vertical" style={{ marginLeft: "20px" }}>

                <header>
                    <h1>Customer Details</h1>
                </header>
                <div>{ReactHtmlParser(state)}</div>

                <Link to="/orders" state={{ id: location.state.id }}>  <button>  Orders</button> </Link>

            </div>
        </React.Fragment>
    );

}

export default CustomerDetails;
