import React from "react";
//import Child from './Child';


class Home extends React.Component {

  constructor() {
    super();
    this.state = { test: 'Customer lists loading...', elements: [] }
  }

  componentDidMount() {
    this.getCustomerList()
  }


  getCustomerList() {
    var table = "";
    for (var i = 0; i < 5; i++) {
      table += `<section id="${i}"><p>
      <Link
      to={{
      pathname: "/child",
      data-name = "jigar"
      }}>  <a href="/child"> Jigar</a> </Link>
        </p>`;
    }

    this.setState({
      test: <div dangerouslySetInnerHTML={{ __html: table }} />
    })
  } 

  render() {
    return (
      <React.Fragment>


       <div  className="flex-vertical" style={{marginLeft: "20px"}}>

       <header>
        <h1>Customer Lists</h1>
      </header>
         {this.state.test}
         </div>      
      </React.Fragment>
    );
  }
}

export default Home;
