import React from "react";
import { useLocation } from "react-router-dom";
import { Link } from 'react-router-dom';
import Orders from "./Orders";
import axios from 'axios';

function api (props){

    getCustomerList()
    {

        axios({
          url: "http://10.10.135.70:8080/customers/(id)",
          method: 'get',
      }).then(res => {
          let data = res.data
          var elements = ``
          data.map((element) => {

          elements += `<section id="${1}"><p>
          <Link
          to={{
          pathname: "/child",
          data-name = "jigar"
          }}>  <a href="/child"> ${element.customerName} </a> </Link>
            </p>`;
          });
          console.log(elements)
          this.setState({
            test: <div dangerouslySetInnerHTML={{ __html: elements }}/>
          })
      }).catch( err => {
          console.log(err)
      });

    }
}

function CustomerDetails(props){
    const navigate = useNavigate();
    api()
    return(

        <React.Fragment>
                    <div  className="flex-vertical" style={{marginLeft: "20px"}}>

                    <header>
                     <h1>Customer Details</h1>
                   </header>

                  {this.state.test}

              <Link
                 to={{
                 pathname: "/orders",
                 }}>  <button href="/orders">  Orders</button> </Link>


                     {/* <button style={{width: "100px"}} onClick={this.orderDetails}>Orders</button> */}
                     </div>      
                  </React.Fragment>

        // <div style={{backgroundImage: `url(${image})`, marginRight:  "20px", padding: "10px", marginBottom: "10px", color: "#fff"}}>
        //     <h1 style={{ cursor: "pointer" }} onClick={() => navigate('/child', {state: {data: props.data}})}>{props.data.title}</h1>
        //     <b style={{display: "block", marginTop: "20px"}}>{props.data.author} | {props.data.date}</b>
        //     <p>{props.data.summary}</p>
        //     <p>{props.data.body}</p>
        // </div>
    );

}

export default CustomerDetails;

